mapper.insert:
insert into board (
	boardnum
	, id
	, title
	, content
)
values (
	board_seq.nextval
	, #{id}
	, #{title}
	, #{content}
)

mapper.delete:
delete
from
	board
where
	boardnum = #{boardnum}
	and id = #{id}

mapper.update:
update
	board
set
	title = #{title}
	, content = #{content}
where
	boardnum = #{boardnum}
	and id = #{id}
